import React, { useState } from 'react';
import { useNavigate, Link } from "react-router-dom";
import { HiUser, HiLockClosed } from "react-icons/hi"; // Importation des icônes de Tailwind CSS
import CopyrightText from '../Components/CopyrightText.jsx';
import { auth } from '../firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { Input } from "@material-tailwind/react";

function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate("/Home");
      })
      .catch((error) => {
        console.error('Erreur de connexion: ', error.message);
        alert('erreur :' + error.message);
      });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center">
      <div className="max-w-md w-full mx-auto p-8">
        <div className="text-center">
          <img className="mx-auto w-20 h-20 mb-4" src="/logo.png" alt="Logo de l'application" />
        </div>
        <form className="mt-8" onSubmit={handleLogin}>
          <div className="mb-3 relative">
            <span className="absolute inset-y-0 left-0 flex items-center pl-3">
              <HiUser className="h-5 w-5 text-gray-400" />
            </span>
            <input
              className="pl-10 rounded-md w-full px-3 py-2 placeholder-gray-400 focus:outline-none focus:ring focus:border-blue-500 border border-gray-300"
              type="text"
              placeholder="Votre Login"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-3 relative">
            <span className="absolute inset-y-0 left-0 flex items-center pl-3">
              <HiLockClosed className="h-5 w-5 text-gray-400" />
            </span>
            <input
              className="pl-10 rounded-md w-full px-3 py-2 placeholder-gray-400 focus:outline-none focus:ring focus:border-blue-500 border border-gray-300"
              type="password"
              placeholder="Votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="flex items-center mb-3">
            <input type="checkbox" className="h-4 w-4 text-blue-500 rounded focus:ring-blue-500" />
            <label className="ml-2 text-sm text-gray-600">Se rappeler de moi</label>
          </div>
          <button
            className="w-full py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-black hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-black"
            type="submit"
          >
            Connexion
          </button>
          <div className="text-sm text-center mt-4">
            <Link to="/password-reset" className="font-medium text-blue-600 hover:text-blue-500">
              Mot de passe oublié ?
            </Link>
          </div>
        </form>
      </div>
      <div className="text-center mt-50">
        <CopyrightText />
      </div>
    </div>
  );
}

export default LoginPage;
