import React, { useState } from 'react';
import { useNavigate, Link } from "react-router-dom";
import CopyrightText from '../Components/CopyrightText.jsx';
import { auth } from '../firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';


function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate("/Home");
      })
      .catch((error) => {
        console.error('Erreur de connexion: ', error.message);
        alert('erreur :' + error.message);
      });
  };

  return (
    <div className="login-page">
      {/* Logo */}
      <div>
        <img className="logo" src="/logo.png" alt="Logo de l'application" />
      </div>
      {/* Formulaire de connexion */}
      <form>
        <div>
          <label htmlFor="username"></label>
          <div>
            <input
              type="text"
              id="username"
              placeholder="Votre Login"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
        </div>
        <div>
          <label htmlFor="password"></label>
          <div className="input-group">
            <input
              type="password"
              id="password"
              placeholder="Votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>
        <div className="form-group">
          <div>
            <input type="checkbox" value="" id="exampleCheckbox" />
            <label>
              Se rappeler de moi
            </label>
          </div>
        </div>
        <Link to="/Home" onClick={handleLogin} >Connection</Link>
        <Link to="/password-reset" >Mot de passe oubli√© ?</Link>
      </form>

      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={handleLogin}>
        Connexion
      </button>
      <CopyrightText />
    </div>
  );
}

export default LoginPage;
