import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { HiUser, HiLockClosed } from 'react-icons/hi'; // Importation des icônes de Tailwind CSS
import { MTRFLogo } from 'material-tailwind'; // Importer le composant de logo depuis Material Tailwind
import { MTInput, MTCheckbox, MTButton } from 'material-tailwind'; // Importer les composants d'entrée, de case à cocher et de bouton depuis Material Tailwind
import { MTCopyrightText } from 'material-tailwind'; // Importer le composant de texte de droits d'auteur depuis Material Tailwind
import { auth } from '../firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';

function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate('/Home');
      })
      .catch((error) => {
        console.error('Erreur de connexion: ', error.message);
        alert('Erreur :' + error.message);
      });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center">
      <div className="max-w-md w-full mx-auto p-8">
        <div className="text-center">
          <MTRFLogo className="mx-auto w-20 h-20 mb-4" /> {/* Utiliser le composant de logo de Material Tailwind */}
        </div>
        <form className="mt-8" onSubmit={handleLogin}>
          <div className="mb-3">
            <MTInput
              type="text"
              placeholder="Votre Login"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              icon={HiUser} // Ajouter l'icône à l'entrée
            />
          </div>
          <div className="mb-3">
            <MTInput
              type="password"
              placeholder="Votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              icon={HiLockClosed} // Ajouter l'icône à l'entrée
            />
          </div>
          <div className="flex items-center mb-3">
            <MTCheckbox label="Se rappeler de moi" /> {/* Utiliser le composant de case à cocher de Material Tailwind */}
          </div>
          <MTButton
            type="submit"
            buttonType="filled"
            color="lightBlue"
            className="w-full text-sm"
          >
            Connexion {/* Utiliser le composant de bouton de Material Tailwind */}
          </MTButton>
          <div className="text-sm text-center mt-4">
            <Link to="/password-reset" className="font-medium text-blue-600 hover:text-blue-500">
              Mot de passe oublié ?
            </Link>
          </div>
        </form>
      </div>
      <div className="text-center mt-50">
        <MTCopyrightText /> {/* Utiliser le composant de texte de droits d'auteur de Material Tailwind */}
      </div>
    </div>
  );
}

export default LoginPage;
