import React, { useState } from 'react';
import { NavLink, Link, useNavigate } from 'react-router-dom'; // Importez useNavigate
import TitleSubtitle from '../Components/TitleSubtitle.jsx';
import CopyrightText from '../Components/CopyrightText';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase-config';
import { BsList } from 'react-icons/bs'; 
import {
  Input,
  Button,
  Navbar, 
  Typography, 
  Dialog,
  DialogHeader,
  DialogBody,
  DialogFooter,
} from "@material-tailwind/react";


function Popup({ onClose }) {
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="absolute inset-0 bg-gray-800 opacity-50"></div> {/* Fond gris en arrière-plan */}
      <div className="bg-white p-8 rounded shadow-lg relative z-10"> {/* Boîte de dialogue en premier plan */}
        <button className="close-btn absolute top-0 right-0 m-4" onClick={onClose}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
        <h2 className="text-2xl font-bold mb-4">Enregistrement réussi</h2>
        <p className="text-gray-700 mb-4">Votre numéro de matricule est :</p>
        <h1 className="text-4xl font-bold text-gray-900">120 903 23</h1>
      </div>
    </div>
  );
}

function Enrollment() {
  const [showNav, setShowNav] = useState(true);
  const navigate = useNavigate(); // Utilisez useNavigate pour obtenir l'objet de navigation


  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        console.log('Déconnexion réussie');
        alert('Déconnexion réussie');
        navigate('/'); // Utilisez l'objet navigate pour effectuer la navigation
      })
      .catch((error) => {
        alert(error.message);
      });
  };

  const [randomNumbers, setRandomNumbers] = useState('');
  useEffect(() => {
    // Génère quatre chiffres aléatoires
    const generateRandomNumbers = () => {
      const randomNumber = Math.floor(1000 + Math.random() * 9000);
      setRandomNumbers(randomNumber.toString().split('').join(' '));
    };

    generateRandomNumbers();
  }, []); // Le tableau vide [] fait en sorte que cela s'exécute une seule fois lors du montage initial

  const [size, setSize] = React.useState(null);
  const handleOpen = (value) => setSize(value);
  const [showMenu, setShowMenu] = useState(false);

  return (
    <div className="enrollment-page flex flex-col h-s:creen">
      {/* Navbar */}
      <nav className="bg-gray-800 py-4 px-8 flex justify-between items-center mb-4">
        <Link to="/" className="text-white text-xl">Goblo</Link>
        <button className="text-white block sm:hidden" onClick={() => setShowNav(!showNav)}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
