import React, { useState } from 'react';
import { useNavigate, Link } from "react-router-dom";
import { Input, Button, Checkbox, Icon } from '@material-tailwind/react';
import { auth } from '../firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';

function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate("/Home");
      })
      .catch((error) => {
        console.error('Erreur de connexion: ', error.message);
        alert('erreur :' + error.message);
      });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center">
      <div className="max-w-md w-full mx-auto p-8">
        <div className="text-center">
          <img className="mx-auto w-10 h-10 mb-4" src="/logo.png" alt="Logo de l'application" />
        </div>
        <form className="mt-8" onSubmit={handleLogin}>
          <div className="relative w-full min-w-[200px] h-10 mb-3">
            <div className="absolute grid w-5 h-5 place-items-center text-blue-gray-500 top-2/4 right-3 -translate-y-2/4">
              <i className="material-icons">person</i>
            </div>
            <Input
              type="text"
              color="gray"
              placeholder="Votre Login"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="relative w-full min-w-[200px] h-10 mb-3">
            <div className="absolute grid w-5 h-5 place-items-center text-blue-gray-500 top-2/4 right-3 -translate-y-2/4">
              <i className="material-icons">lock</i>
            </div>
            <Input
              type="password"
              color="gray"
              placeholder="Votre mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="flex items-center mb-3">
            <Checkbox color="dark" />
            <label className="ml-2 text-sm text-gray-600">Se rappeler de moi</label>
          </div>
          <Button
            color="black"
            size="lg"
            type="submit"
            ripple="light"
            className="w-full"
          >
            Connexion
          </Button>
          <div className="text-sm text-center mt-4">
            <Link to="/password-reset" className="font-medium text-dark-600 hover:text-dark-500">
              Mot de passe oubli√© ?
            </Link>
          </div>
        </form>
      </div>
      <div className="text-center mt-50">
        {/* Le composant CopyrightText n'est pas fourni dans l'exemple */}
      </div>
    </div>

  );
}

export default LoginPage;
