import React, { useState } from 'react';
import { auth } from '../firebase-config';
import { sendPasswordResetEmail } from 'firebase/auth'; // Importez la fonction sendPasswordResetEmail depuis Firebase

function PasswordReset() {
  const [email, setEmail] = useState('');
  const [resetSent, setResetSent] = useState(false);
  const [error, setError] = useState(null);

  const handleResetPassword = async (e) => {
    e.preventDefault();
    try {
      await sendPasswordResetEmail(auth, email); // Utilisez sendPasswordResetEmail avec l'objet auth et l'email
      setResetSent(true);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      {resetSent ? (
        <p>Un email de réinitialisation du mot de passe a été envoyé à {email}.</p>
      ) : (
        <form onSubmit={handleResetPassword} className="space-y-6">
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">Adresse e-mail</label>
            <div className="mt-1">
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                placeholder="Entrez votre adresse e-mail"
              />
            </div>
          </div>
          <div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Réinitialiser le mot de passe
            </button>
          </div>
          {error && <p className="text-red-600">{error}</p>}
        </form>
      )}
    </div>
  );
}

export default PasswordReset;
