function Popup({ onClose }) {
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="absolute inset-0 bg-gray-800 opacity-50"></div>
      <div className="bg-white p-8 rounded shadow-lg relative z-10"> {/* Boîte de dialogue en premier plan */}
        <button className="close-btn absolute top-0 right-0 m-4" onClick={onClose}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
        <h2 className="text-2xl font-bold mb-4">Enregistrement réussi</h2>
        <p className="text-gray-700 mb-4">Votre numéro de matricule est :</p>
        <h1 className="text-4xl font-bold text-gray-900">120 903 23</h1>
      </div>
    </div>
  );
}

function Enrollment() {
  const [showPopup, setShowPopup] = useState();
  const [showNav, setShowNav] = useState(true);
  const navigate = useNavigate(); // Utilisez useNavigate pour obtenir l'objet de navigation

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  const handleEnregistrerClick = (event) => {
    event.preventDefault(); // Empêcher le formulaire de se soumettre automatiquement
    setShowPopup(true);
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        console.log('Déconnexion réussie');
        alert('Déconnexion réussie');
        navigate('/'); // Utilisez l'objet navigate pour effectuer la navigation
      })
      .catch((error) => {
        alert(error.message);
      });
  };

  return (
    <div className="enrollment-page flex flex-col h-screen"> {/* Correction de la classe h-s:creen en h-screen */}
      {/* Navbar */}
      <nav className="bg-gray-800 py-4 px-8 flex justify-between items-center mb-4">
        <Link to="/" className="text-white text-xl">Goblo</Link>
        <button className="text-white block sm:hidden" onClick={() => setShowNav(!showNav)}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
          </svg>
        </button>
        <div className="hidden sm:flex items-center space-x-4">
          <NavLink to="/encaisser" activeClassName="font-semibold" className="text-white mx-2">Encaisser</NavLink>
          <NavLink to="/reports" activeClassName="font-semibold" className="text-white mx-2">Rapports</NavLink>
          <NavLink to="/accounts" activeClassName="font-semibold" className="text-white mx-2">Comptes</NavLink>
          <NavLink to="/control" activeClassName="font-semibold" className="text-white mx-2">Contrôler</NavLink>
          <NavLink to="/withdraw" activeClassName="font-semibold" className="text-white mx-2">Décaisser</NavLink>
          <button onClick={handleLogout} className="text-white mx-2">Déconnexion</button>
        </div>
        <div className={`${showNav ? 'block' : 'hidden'} sm:hidden bg-white text-gray-800 absolute top-12 left-0 right-0 py-2 px-4 space-y-2 border border-gray-300 rounded-md`}>
          <NavLink to="/encaisser" activeClassName="font-semibold" className="block py-2">Encaisser</NavLink>
          <NavLink to="/reports" activeClassName="font-semibold" className="block py-2">Rapports</NavLink>
          <NavLink to="/accounts" activeClassName="font-semibold" className="block py-2">Comptes</NavLink>
          <NavLink to="/control" activeClassName="font-semibold" className="block py-2">Contrôler</NavLink>
          <NavLink to="/withdraw" activeClassName="font-semibold" className="block py-2">Décaisser</NavLink>
          <button onClick={handleLogout} className="block py-2">Déconnexion</button>
        </div>
      </nav>

      {/* Logo */}
      <div className="text-center mb-4">
        <img src="/logo.png" alt="Logo de Goblo" className="h-16 mx-auto" />
      </div>

      {/* Titre et sous-titre centrés */}
      <div className="text-center mb-4">
        <TitleSubtitle title="Enroller" subtitle="les contribuables" className="title" />
      </div>

      {/* Formulaire d'enrôlement */}
      <form className="enrollment-form flex flex-col space-y-4 px-4 md:px-0 md:max-w-lg mx-auto">
        <input type="text" placeholder="Nom et prénom*" className="input-nom-prenom border border-gray-300 rounded-md py-2 px-4" />
        <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
          <div className="w-full md:w-1/2">
            <select className="select-zone border border-gray-300 rounded-md py-2 px-4 w-full">
              <option value="" disabled selected>Zone</option>
              <option value="zone1">Zone 1</option>
              <option value="zone2">Zone 2</option>
            </select>
          </div>
          <div className="w-full md:w-1/2">
            <select className="select-secteur border border-gray-300 rounded-md py-2 px-4 w-full">
              <option value="" disabled selected>Secteur</option>
              <option value="secteurA">Secteur A</option>
              <option value="secteurB">Secteur B</option>
            </select>
          </div>
        </div>
        <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
          <input type="text" placeholder="Téléphone 1" className="input-telephone border border-gray-300 rounded-md py-2 px-4 w-full" />
          <input type="text" placeholder="Téléphone 2" className="input-telephone border border-gray-300 rounded-md py-2 px-4 w-full" />
        </div>
        <input type="text" placeholder="Montant" className="input-montant border border-gray-300 rounded-md py-2 px-4" />
        <select className="select-categorie border border-gray-300 rounded-md py-2 px-4">
          <option value="" disabled selected>Catégorie</option>
          <option value="categorie1">Catégorie 1</option>
          <option value="categorie2">Catégorie 2</option>
        </select>
        {/* Changer la couleur du bouton */}
        <button className="bout bg-black text-white font-bold py-2 md:py-3 px-4 md:px-6 rounded mt-4 w-full md:w-auto" onClick={handleEnregistrerClick}>Enregistrer</button>
      </form>

      {/* Pop-up */}
      {showPopup && <Popup onClose={handleClosePopup} />}

      {/* Composant pour le droit d'auteur */}
      <div className="mt-auto text-center">
        <CopyrightText />
      </div>
    </div>
  );
}

export default Enrollment;
