import React, { useState } from 'react';
import { useNavigate, Link } from "react-router-dom";
import { HiUser, HiLockClosed } from "react-icons/hi"; // Importation des icônes de Tailwind CSS
import CopyrightText from '../Components/CopyrightText.jsx';
import { auth } from '../firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';

// Importation du composant "Sign In" de Material Tailwind
import { Card, Input, Checkbox, Button } from '@material-tailwind/react';

function LoginPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log(userCredential);
        navigate("/Home");
      })
      .catch((error) => {
        console.error('Erreur de connexion: ', error.message);
        alert('erreur :' + error.message);
      });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center">
      <div className="max-w-md w-full mx-auto p-8">
        <div className="text-center">
          <img className="mx-auto w-20 h-20 mb-4" src="/logo.png" alt="Logo de l'application" />
        </div>
        <form className="mt-8" onSubmit={handleLogin}>
          {/* Remplacement du formulaire par le composant "Sign In" de Material Tailwind */}
          <Card>
            <Card.Header>
              <h3 className="block font-sans text-3xl antialiased font-semibold leading-snug tracking-normal text-white">
                Sign In
              </h3>
            </Card.Header>
            <Card.Body>
              <Input
                color="blue"
                size="regular"
                outline={false}
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <Input
                color="blue"
                size="regular"
                outline={false}
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <Checkbox color="blue" id="remember-me" />
              <label htmlFor="remember-me" className="font-light text-gray-700 cursor-pointer select-none">
                Remember Me
              </label>
            </Card.Body>
            <Card.Footer>
              <Button
                color="blue"
                buttonType="filled"
                size="regular"
                block={true}
                ripple="light"
                onClick={handleLogin}
              >
                Sign In
              </Button>
              <p className="flex justify-center mt-6 font-sans text-sm antialiased font-light leading-normal text-inherit">
                Don't have an account? <Link to="/signup" className="ml-1 font-bold text-blue-gray-900">Sign up</Link>
              </p>
            </Card.Footer>
          </Card>
        </form>
      </div>
      <div className="text-center mt-50">
        <CopyrightText />
      </div>
    </div>
  );
}

export default LoginPage;
