import React, { useState } from 'react';
import TitleSubtitle from '../Components/TitleSubtitle.jsx';  
import CopyrightText from '../Components/CopyrightText.jsx'; 

function Popup({ onClose }) {
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="absolute inset-0 bg-gray-800 opacity-50"></div>
      <div className="bg-white p-8 rounded shadow-lg">
        <button className="close-btn absolute top-0 right-0 m-4" onClick={onClose}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
        <h2 className="text-2xl font-bold mb-4">Encaissement réussi</h2>
        <p>MR TETCHI a payé</p>
        <h1>10 000 FCFA</h1>
        <h5>et a le reçu N°</h5>
        <h1>2 3 7 6</h1>
      </div>
    </div>
  );
}

function EncaisserPage() {
  const [showPopup, setShowPopup] = useState(false);

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  const handleEncaisserClick = () => {
    // Logique pour effectuer l'encaissement

    // Afficher le pop-up
    setShowPopup(true);
  };

  return (
    <div className="encaisser-page flex flex-col items-center justify-center">
      {/* Navbar */}
      <nav className="bg-gray-800 py-4 px-8 w-full flex justify-between items-center mb-4">
        <img src="/logo.png" alt="Logo de l'entreprise" className="h-12" />
        {/* Liens de navigation */}
        <div className="text-white space-x-4">
          <a href="#" className="text-white">Lien 1</a>
          <a href="#" className="text-white">Lien 2</a>
          <a href="#" className="text-white">Lien 3</a>
        </div>
      </nav>

      {/* Titre et sous-titre */}
      <TitleSubtitle title="Encaisser" subtitle="les contribuables" />

      {/* Champ de saisie pour le matricule */}
      <div>
        <input type="text" placeholder="Matricule" className="matricule-input border border-gray-300 rounded-md py-2 px-4 mb-4" />
      </div>

      {/* Bouton "Encaisser" */}
      <div>
        <button className='bout bg-black text-white font-bold py-2 md:py-3 px-4 md:px-6 rounded' onClick={handleEncaisserClick}>Encaisser</button>
      </div>

      {/* Pop-up */}
      {showPopup && <Popup onClose={handleClosePopup} />}

      {/* Composant pour le droit d'auteur */}
      <div className="mt-auto">
        <CopyrightText />
      </div>
    </div>
  );
}

export default EncaisserPage;
